mapper.select:
select *
from t_order
where order_no = #{orderNo}
and user_id = #{userId}

mapper.update:
update t_order
set status = #{status},
    update_time = #{updateTime}
where id = #{id}
  and status = 0

update t_order
set status = 2,
    close_time = #{closeTime},
    update_time = #{closeTime}
where id = #{id}
  and status = 0
  and order_no = #{orderNo}
