mapper.update:
UPDATE function_event_outbox
SET status = 1
WHERE id = #{id}

UPDATE function_event_outbox
SET retry_count = retry_count + 1,
    next_retry_time = #{nextRetryTime}
WHERE id = #{id}

UPDATE function_event_outbox
SET status = 2,
    retry_count = #{retryCount},
    next_retry_time = #{nextRetryTime},
    error_msg = #{errorMsg}
WHERE id = #{id}
